package main

import (
	"encoding/json"
	"fmt"
	ps "github.com/httpreserve/phantomjsscreenshot"
	"io/ioutil"
	"log"
	"strings"
)

// Template fields used in the sitemap JSON.
const baseURLTemplate = "{base_url}"
const transferIDTemplate = "{transfer_uuid}"
const aipIDTemplate = "{aip_uuid}"

// Dimensions for screenshot output.
const dimensionX = 768
const dimensionY = 430

// AMUrls is a descriptive structure for Archivematica URLs.
type AMUrls struct {
	Title string
	URL   string
}

// DashboardTab is a descriptive structure for Archivematica Dashboard Tabs.
type DashboardTab struct {
	Title       string
	Description string
	Urls        []AMUrls
}

// Tab is a generic wrapper for the Archivematica Dashboard Tab Type.
type Tab struct {
	DashboardTab DashboardTab
}

// AMSiteMap is a descriptive strucutre for the Archivematica Sitemap.
type AMSiteMap struct {
	Title                string
	Version              string
	Description          string
	BaseURL              string
	TransferUUID         string
	AipUUID              string
	MicroserviceExamples []map[string]string
	Tabs                 []Tab
}

// SiteMap is a generic wrapper for the Archivematica Sitemap Type.
type SiteMap struct {
	ArchivematicaSitemap AMSiteMap
}

// replaceMicroservices is an even more blunt object that loops through each
// of the microservice template fields and tries to replace them, even if we
// have already completed that before. Consider using a flag, and alternative
// checking mechanisms in future versions.
func replaceMicroservices(captureURL string,
	microserviceExamples []map[string]string) string {
	for index := range microserviceExamples {
		for key, value := range microserviceExamples[index] {
			if strings.Contains(captureURL, key) {
				return strings.Replace(captureURL, key, value, 1)
			}
			break
		}
	}
	return captureURL
}

// templateReplace is a pretty blunt object that tries to replace all the
// strings whether there is a template field in one or not. Consider creating
// a different kind of mapping in future versions.
func templateReplace(templateURL string, baseURL string,
	transferUUID string, aipUUID string,
	microserviceExamples []map[string]string) string {
	captureURL := strings.Replace(templateURL, baseURLTemplate, baseURL, 1)
	captureURL = strings.Replace(captureURL, transferIDTemplate, transferUUID, 1)
	captureURL = strings.Replace(captureURL, aipIDTemplate, aipUUID, 1)
	return replaceMicroservices(captureURL, microserviceExamples)
}

func outputNewline() {
	fmt.Println("\n\n")
}

func outputMarkdownHeader(title string, version string,
	description string) {
	amheader := "data:image/png;base64,"
	fmt.Println(fmt.Sprintf("<img src=\"%s\" />", amheader))
	fmt.Println(fmt.Sprintf("# %s", title))
	fmt.Println(fmt.Sprintf("##### Version %s", version))
	fmt.Println(fmt.Sprintf("%s", description))
	outputNewline()
}

func outputLoginPage() {
	loginPage := "data:image/jpeg;base64,"
	fmt.Println("# Login Page")
	fmt.Println(fmt.Sprintf("<img src=\"%s\" />", loginPage))
	fmt.Println("Archivematica users are greeted with this login page.")
	outputNewline()
}

func outputDashboardTabHeader(title string, description string) {
	fmt.Println(fmt.Sprintf("## %s", title))
	fmt.Println(fmt.Sprintf("%s", description))
	outputNewline()
}

func outputSubTabHeader(title string) {
	fmt.Println(fmt.Sprintf("### %s", title))
	outputNewline()
}

func capture(url string, title string, debug bool) string {
	log.Println("Outputting:", url)
	imgOpen := fmt.Sprintf("<img alt=\"%s\" src=\"", title)
	imgClose := "\" />"
	capture := "data:image/png;base64,"
	if !debug {
		var err error
		capture, err = ps.GrabScreenshot(url, dimensionX, dimensionY)
		if err != nil {
			log.Fatal(err)
		}
	}
	return fmt.Sprintf("%s%s%s", imgOpen, capture, imgClose)
}

func main() {

	log.Println("Waking up.")

	byt, err := ioutil.ReadFile("sitemap.json")
	if err != nil {
		log.Fatal("Cannot read sitemap file.")
	}

	var js SiteMap
	json.Unmarshal(byt, &js)

	if js.ArchivematicaSitemap.Title == "" {
		log.Fatal("Exiting early, JSON does not seem to have been parsed, the structure.")
	}

	title := js.ArchivematicaSitemap.Title
	baseURL := js.ArchivematicaSitemap.BaseURL
	version := js.ArchivematicaSitemap.Version
	description := js.ArchivematicaSitemap.Description
	transferUUID := js.ArchivematicaSitemap.TransferUUID
	aipUUID := js.ArchivematicaSitemap.AipUUID
	microserviceExamples := js.ArchivematicaSitemap.MicroserviceExamples

	log.Println(title, version, baseURL, transferUUID, aipUUID)

	// Output the opening section of the Markdown we want to use and the login
	// page which for now we've hard coded.
	outputMarkdownHeader(title, version, description)
	outputLoginPage()

	// Run through each of the tabs in order to produce a walkthrough of the
	// system.
	tabs := js.ArchivematicaSitemap.Tabs
	for _, tab := range tabs {
		outputDashboardTabHeader(tab.DashboardTab.Title,
			tab.DashboardTab.Description)
		for _, url := range tab.DashboardTab.Urls {
			captureURL := templateReplace(url.URL, baseURL, transferUUID,
				aipUUID, microserviceExamples)
			outputSubTabHeader(url.Title)
			fmt.Println(capture(captureURL, url.Title, false))
		}
	}

}
